import mongoose from 'mongoose';

const CartSchema = new mongoose.Schema({

    userId : { type : String , required : true},
    products : [
        {
            productId:{
                type : String , required: true
            },
        
            quantity : {
                type : Number ,
                default : 1 ,
            }
        },
]


},{
    timestamps : true
});

export const CartModel =  mongoose.model("Cart", CartSchema);

